mapper.select:

where 1 = 1

	AND ol.title like concat('%', #{title}, '%')


	AND ol.business_type = #{businessType}


	AND ol.business_type in

		#{businessType}



	AND ol.status = #{status}


	AND u.user_name like concat('%', #{userName}, '%')


	AND u.nick_name like concat('%', #{userNick}, '%')


	and date_format(ol.oper_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


	and date_format(ol.oper_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


${params.dataScope}
order by ol.oper_id desc

mapper.delete:
 		update sys_oper_log
 		set del_flag = 1
 		where oper_id in

 			#{operId}


		${params.updateScope}
