mapper.select:

where rsm.role_id = #{search.roleId}

    and rsm.system_menu_id in

        #{item.menuId}

    and rsm.system_menu_id not in

        #{item.systemId}



    and 1 != 1


${params.dataScope}

mapper.delete:
delete from sys_role_system_menu where role_id in

    #{roleId}


${params.updateScope}
